using i32 = std::int32_t;
using i64 = std::int64_t;
//const i32 INF = 1000000000 + 7;
const i32 fastio_ = ([](){std::ios_base::sync_with_stdio(0); std::cin.tie(0);return 0;})();

i32 find(vector<i32>& parent, i32 i) {
    if (parent[i] == -1) return i;
    return parent[i] = find(parent, parent[i]);
}

void join(vector<i32>& parent, i32 i, i32 j) {
    i = find(parent, i);
    j = find(parent, j);
    if (i == j) return;
    parent[i] = j;
}

vector<bool> sol(i32 n, i32 threshold, vector<vector<i32>> queries) {
    vector<i32> parent(n + 1, -1);
    for (i32 i = threshold + 1; i <= n; ++i) {
        for (int j = i + i; j <= n; j += i) {
            join(parent, i, j);
        }
    }
    vector<bool> ans;
    for (const auto& query : queries) {
        i32 root1 = find(parent, query[0]);
        i32 root2 = find(parent, query[1]);
        ans.push_back(root1 == root2);
    }
    return ans;
}

